import type { FileSystem } from '@stackpress/lib/types';
import type { PluginConfig, SchemaConfig } from '@stackpress/idea-parser/types';
import type Terminal from './Terminal.js';
import type Transformer from './Transformer.js';
export type CLIProps = {
    cli: Terminal;
};
export type FileLoaderOptions = {
    cwd?: string;
    fs?: FileSystem;
};
export type TerminalOptions = FileLoaderOptions & {
    brand?: string;
    extname?: string;
};
export type PluginProps<T extends {}> = T & {
    config: PluginConfig;
    schema: SchemaConfig;
    transformer: Transformer<{}>;
    cwd: string;
};
export type PluginWithCLIProps = PluginProps<CLIProps>;
