export default function set(data) {
    const store = new Set(data);
    const callable = Object.assign((index) => Array.from(store.values())[index], {
        add: (value) => store.add(value),
        clear: () => store.clear(),
        delete: (value) => store.delete(value),
        entries: () => store.entries(),
        forEach: (callback) => store.forEach(callback),
        has: (value) => store.has(value),
        index: (index) => Array.from(store.values())[index],
        values: () => store.values()
    });
    Object.defineProperty(callable, 'size', { get: () => store.size });
    return callable;
}
;
